import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-splash-screen',
  template: `
    <div 
      class="fixed inset-0 bg-white z-50 flex items-center justify-center transition-opacity duration-500"
      [class.opacity-0]="!isVisible"
      [class.pointer-events-none]="!isVisible">
      <div class="text-center">
        <h1 class="text-6xl font-bold mb-4 animate-pulse">
          <span class="text-black">MA</span>
          <span class="text-blue-600">.</span>
        </h1>
        <div class="flex justify-center space-x-2">
          <div class="w-3 h-3 bg-blue-600 rounded-full animate-bounce" style="animation-delay: 0s"></div>
          <div class="w-3 h-3 bg-blue-600 rounded-full animate-bounce" style="animation-delay: 0.2s"></div>
          <div class="w-3 h-3 bg-blue-600 rounded-full animate-bounce" style="animation-delay: 0.4s"></div>
        </div>
      </div>
    </div>
  `,
  styles: [`
    :host {
      display: block;
    }
  `]
})
export class SplashScreenComponent implements OnInit {
  isVisible = true;

  ngOnInit() {
    // Hide splash screen after 2 seconds
    setTimeout(() => {
      this.isVisible = false;
    }, 2000);
  }
}
